element.calculation:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	var firstName = current.variables.first_name.trim();
	var lastName = current.variables.last_name.trim();

	if(firstName && lastName){
		var gr = new GlideRecord("sys_user");
		gr.addQuery("first_name", firstName);
		gr.addQuery("last_name", lastName);
		gr.query();
		if (gr.next()) {
			current.subject_person_hr_profile = gr.getUniqueValue();
		}
	}

})(current, previous);
