sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if ( newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below

	var os = g_form.getValue('os_version');

	g_form.clearOptions('os_version');

	if(newValue=='windows'){
		g_form.addOption('os_version','Windows Server 2008','Windows Server 2008');
		g_form.addOption('os_version','Windows Server 2012','Windows Server 2012');
		g_form.addOption('os_version','Windows Server 2016','Windows Server 2016');
		g_form.addOption('os_version','Windows Server 2019','Windows Server 2019');
		g_form.addOption('os_version','Windows Server 2022','Windows Server 2022');
	}

		if(newValue=='android'){
		g_form.addOption('os_version','7','7');
		g_form.addOption('os_version','8','8');
		g_form.addOption('os_version','9','9');
		g_form.addOption('os_version','10','10');
		g_form.addOption('os_version','11','11');
	}
	if(newValue=='linux'){
		g_form.addOption('os_version','u7','u7');
		g_form.addOption('os_version','u8','u8');
		g_form.addOption('os_version','u9','u9');
		g_form.addOption('os_version','u10','u10');
		g_form.addOption('os_version','u11','u11');
	}

	if(newValue=='other'){
		g_form.addOption('os_version','other','other');
	}

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('os_version',os);
	}
}
